const helper   = require('node-red-viseo-helper');
const Account  = require('ethjs-account')

// --------------------------------------------------------------------------
//  NODE-RED
// --------------------------------------------------------------------------

module.exports = function(RED) {
    const register = function(config) {
        RED.nodes.createNode(this, config);
        let node = this;

        start(node, RED, config);
        this.on('input', (data)  => { input(node, data, config) });
    }
    RED.nodes.registerType("ethjs-account", register, {});
}

const start = (node, RED, config) => {}

const input = (node, data, config) => {
    let mnemonic = ''
    let entropy  = ''
    if (config.input){
        entropy = config.input
        if (config.inputType === 'msg'){ entropy = helper.getByString(data, entropy) }
    } else {
        mnemonic = [];
        for (;mnemonic.length < 12;){
            let idx  = Math.round(Math.random() * WORDS_LIST.length)
            let word = WORDS_LIST[idx]
            if (mnemonic.indexOf(word)<0){ mnemonic.push(word); }
        }
        mnemonic = mnemonic.join(' ');
        entropy = mnemonicToEntropy(mnemonic)
    }

    let wallet  = Account.generate(entropy)
    wallet.mnemonic = mnemonic;
    helper.setByString(data, config.output, wallet);
    node.send(data)
}

// --------------------------------------------------------------------------
//  UTILITY
//  From: https://github.com/ethers-io/ethers.js
// --------------------------------------------------------------------------


let mnemonicToEntropy = (mnemonic) => {
    let words = mnemonic.toLowerCase().split(' ');
    if ((words.length % 3) !== 0) { throw new Error('invalid mnemonic'); }
 
    let entropy = arrayify(new Uint8Array(Math.ceil(11 * words.length / 8)));
 
    let offset = 0;
    for (let i = 0; i < words.length; i++) {
        let index = WORDS_LIST.indexOf(words[i]);
        if (index === -1) { throw new Error('invalid mnemonic'); }
 
        for (let bit = 0; bit < 11; bit++) {
            if (index & (1 << (10 - bit))) {
                entropy[offset >> 3] |= (1 << (7 - (offset % 8)));
            }
            offset++;
        }
    }
    let entropyBits  = 32 * words.length / 3;

    /*
    let checksumBits = words.length / 3;
    let checksumMask = getUpperMask(checksumBits);
    let checksum = arrayify(sha256(entropy.slice(0, entropyBits / 8)))[0];
    checksum &= checksumMask;
 
    let checktest = (entropy[entropy.length - 1] & checksumMask)
    if (checksum !== checktest) {
        console.log('Invalid Checksum', checksum, checktest);
        //throw new Error('invalid checksum');
    }*/
    return hexlify(entropy.slice(0, entropyBits / 8));
}

let getUpperMask = (bits) => {
    return ((1 << bits) - 1) << (8 - bits);
 }

let arrayify = (value, name) => {
    
    if (value && value.toHexString) {
        value = value.toHexString();
    }

    if (isHexString(value)) {
        value = value.substring(2);
        if (value.length % 2) { value = '0' + value; }

        let result = [];
        for (let i = 0; i < value.length; i += 2) {
            result.push(parseInt(value.substr(i, 2), 16));
        }

        return addSlice(new Uint8Array(result));
    }

    if (isArrayish(value)) {
        return addSlice(new Uint8Array(value));
    }

    throw new Error('invalid arrayify value', { name: name, input: value });
}

const HexCharacters = '0123456789abcdef';
let hexlify = (value, name) => {
    
    if (value && value.toHexString) {
        return value.toHexString();
    }

    if (typeof(value) === 'number') {
        if (value < 0) {
            throw new Error('cannot hexlify negative value', { name: name, input: value });
        }

        var hex = '';
        while (value) {
            hex = HexCharacters[value & 0x0f] + hex;
            value = parseInt(value / 16);
        }

        if (hex.length) {
            if (hex.length % 2) { hex = '0' + hex; }
            return '0x' + hex;
        }

        return '0x00';
    }

    if (isHexString(value)) {
        if (value.length % 2) {
            value = '0x0' + value.substring(2);
        }
        return value;
    }

    if (isArrayish(value)) {
        var result = [];
        for (var i = 0; i < value.length; i++) {
                var v = value[i];
                result.push(HexCharacters[(v & 0xf0) >> 4] + HexCharacters[v & 0x0f]);
        }
        return '0x' + result.join('');
    }

    throw new Error('invalid hexlify value', { name: name, input: value });
}

let isHexString = (value, length) => {
    if (typeof(value) !== 'string' || !value.match(/^0x[0-9A-Fa-f]*$/)) {
        return false
    }
    if (length && value.length !== 2 + 2 * length) { return false; }
    return true;
}

let isArrayish = (value) => {
    if (!value || parseInt(value.length) != value.length || typeof(value) === 'string') {
        return false;
    }

    for (var i = 0; i < value.length; i++) {
        var v = value[i];
        if (v < 0 || v >= 256 || parseInt(v) != v) {
            return false;
        }
    }

    return true;
}

let addSlice = (array) => {
    if (array.slice) { return array; }

    array.slice = function() {
        var args = Array.prototype.slice.call(arguments);
        return new Uint8Array(Array.prototype.slice.apply(array, args));
    }

    return array;
}

const WORDS = ""

const WORDS_LIST = WORDS.replace(/([A-Z])/g, ' $1').toLowerCase().substring(1).split(' ');


// ------------------------------------------
//  SHA 256
// ------------------------------------------

const hash = require('hash.js');

let sha256 = (data) => {
    data = arrayify(data);
    return '0x' + (hash.sha256().update(data).digest('hex'));
}

let sha512 = (data) => {
    data = arrayify(data);
    return '0x' + (hash.sha512().update(data).digest('hex'));
}
